import React from "react";
import { Col } from "reactstrap";
import { Link } from "react-router-dom";
import "../../styles/car-item.css";

const CarItem = (props) => {
  const { imgUrl, model, carName, automatic, speed, price } = props.item;

  return (
    <Col lg="4" md="4" sm="6" className="mb-5">
      <div className="car__item">
        <div className="car__img">
          <img src={imgUrl} alt={`Автомобиль ${carName}`} className="w-100" />
        </div>

        <div className="car__item-content mt-4">
          <h4 className="section__title text-center">{carName}</h4>
          <h6 className="rent__price text-center mt-3">
            ${price}.00 <span>/ сутки</span>
          </h6>

          <div className="car__item-info d-flex align-items-center justify-content-between mt-3 mb-4">
            <span className="d-flex align-items-center gap-1">
              <i className="ri-car-line"></i> {model}
            </span>
            <span className="d-flex align-items-center gap-1">
              <i className="ri-settings-2-line"></i> {automatic ? 'Автомат' : 'Механика'}
            </span>
            <span className="d-flex align-items-center gap-1">
              <i className="ri-timer-flash-line"></i> {speed}
            </span>
          </div>

          <div className="d-flex gap-3">
            <button className="car__item-btn car__btn-rent">
              <Link to={`/cars/${carName}`}>Арендовать</Link>
            </button>

            <button className="car__item-btn car__btn-details">
              <Link to={`/cars/${carName}`}>Подробнее</Link>
            </button>
          </div>
        </div>
      </div>
    </Col>
  );
};

export default CarItem;
